// Extract key email information
const emailData = {
    from: msg.from || 'Unknown Sender',
    to: msg.to || 'Unknown Recipient', 
    subject: msg.subject || 'No Subject',
    text: msg.text || msg.payload || 'No Content',
    date: msg.date || new Date().toISOString(),
    messageId: msg.messageId || 'unknown'
};

// Build prompt for DeepSeek, emphasizing current email priority
const prompt = `Please analyze the following email, focusing on the latest content sent, with historical correspondence only as context reference:

üìß Email Details:
From: ${emailData.from}
To: ${emailData.to}
Subject: ${emailData.subject}
Sent Time: ${emailData.date}
Full Content: ${emailData.text}

üéØ Analysis Focus (by priority):
1. **Current Email Core Content**: Focus on analyzing the sender's latest intent and needs in this email
2. **Historical Context Reference**: Use historical correspondence to understand background, but don't let historical content interfere with judgment of current email
3. **Timeliness Assessment**: Prioritize identifying items in current email that need immediate attention

üìã Please provide the following analysis, each within 30 words:
1. [Intent Analysis]: Main purpose of email and sender's real needs
2. [Key Information]: Key points to focus on, such as time, place, people, etc.
3. [Suggested Reply]: Provide specific reply content (concise, professional, targeted)

Please reply in English, keep it concise, professional and practical, no unnecessary words.
‚ö†Ô∏è Important: Ensure all analysis focuses on the current email, with historical information only used to supplement background understanding.`;

// Save original email information
msg.originalEmail = emailData;
msg.analysisPrompt = prompt;

// Add logging
node.log(`Received new email - From: ${emailData.from}, Subject: ${emailData.subject}`);

return msg;

// Get DeepSeek API Key from environment variable
const API_KEY = process.env.DEEPSEEK_API_KEY || "";

// Validate API key
if (!API_KEY) {
    node.error("DEEPSEEK_API_KEY environment variable is not set. Please configure it in .env file or environment variables.");
    msg.payload = {
        error: true,
        message: "API key not configured. Please set DEEPSEEK_API_KEY environment variable."
    };
    return msg;
}

// Build request headers
msg.headers = {
    "Content-Type": "application/json",
    "Authorization": `Bearer ${API_KEY}`
};

// Build request body
msg.payload = {
    "model": "deepseek-chat",
    "messages": [
        {
            "role": "system",
            "content": "You are an experienced and highly focused email processing expert and business communication consultant. You excel at quickly understanding email intent and providing appropriate, efficient reply suggestions. Emails may be written in Chinese/English, but you need to analyze them uniformly in English for me"
        },
        {
            "role": "user",
            "content": msg.analysisPrompt
        }
    ],
    "max_tokens": 1000,
    "temperature": 0.2
};

// Set request method and URL
msg.method = "POST";
msg.url = "https://api.deepseek.com/v1/chat/completions";

return msg;

// Check response status
if (msg.statusCode !== 200) {
    msg.payload = {
        error: true,
        statusCode: msg.statusCode,
        message: "AI analysis failed",
        originalEmail: msg.originalEmail,
        timestamp: new Date().toISOString()
    };
    node.error(`API call failed: ${msg.statusCode}`);
    return msg;
}

// Extract AI analysis results
if (msg.payload && msg.payload.choices && msg.payload.choices.length > 0) {
    const aiAnalysis = msg.payload.choices[0].message.content;

    // Build final result
    msg.payload = {
        success: true,
        timestamp: new Date().toISOString(),
        emailSummary: {
            from: msg.originalEmail.from,
            subject: msg.originalEmail.subject,
            preview: msg.originalEmail.text.substring(0, 150) + (msg.originalEmail.text.length > 150 ? '...' : ''),
            date: msg.originalEmail.date,
            messageId: msg.originalEmail.messageId
        },
        fullEmailContent: msg.originalEmail.text,
        aiAnalysis: aiAnalysis,
        tokenUsage: msg.payload.usage || {}
    };

    // Set notification title
    msg.topic = `üìß New Email AI Analysis Complete - ${msg.originalEmail.subject}`;

    node.log(`AI analysis complete - Email: ${msg.originalEmail.subject}`);

} else {
    msg.payload = {
        error: true,
        message: "AI response format abnormal",
        originalEmail: msg.originalEmail,
        rawResponse: msg.payload
    };
    node.error("AI response format abnormal");
}

return msg;

// Only process successful analysis results
if (!msg.payload.success) {
    return null;
}

// Save original analysis result
msg.originalAnalysis = msg.payload;

// Get DeepSeek API Key from environment variable
const API_KEY = process.env.DEEPSEEK_API_KEY || "";

// Validate API key
if (!API_KEY) {
    node.error("DEEPSEEK_API_KEY environment variable is not set. Please configure it in .env file or environment variables.");
    msg.payload = {
        error: true,
        message: "API key not configured. Please set DEEPSEEK_API_KEY environment variable."
    };
    return msg;
}

// Build request headers
msg.headers = {
    "Content-Type": "application/json",
    "Authorization": `Bearer ${API_KEY}`
};

// Build specialized prompt for extracting intent and action
const extractPrompt = `Please extract the three most core pieces of information from the following email analysis result:

${msg.payload.aiAnalysis}

Please output strictly in the following format, do not add any other content:
Intent: [One sentence summarizing the real intent of the email]
Key Information: [Key points to focus on, such as time, place, people, etc.]
Action: [One sentence explaining what I should do]

Requirements:
- Each item no more than 30 characters
- Give conclusions directly, no explanations
- Express in the most concise English`;

// Build request body
msg.payload = {
    "model": "deepseek-chat",
    "messages": [
        {
            "role": "system",
            "content": "You are an information extraction expert, skilled at extracting key information from complex text and expressing it in the most concise language."
        },
        {
            "role": "user",
            "content": extractPrompt
        }
    ],
    "max_tokens": 200,
    "temperature": 0.1
};

// Set request method and URL
msg.method = "POST";
msg.url = "https://api.deepseek.com/v1/chat/completions";

return msg;

// Check response status
if (msg.statusCode !== 200) {
    // Extraction failed, use default values
    msg.payload = {
        intent: "Email needs processing",
        keyInfo: "View detailed content",
        action: "View detailed analysis"
    };
    node.warn("AI extraction failed, using default values");
} else if (msg.payload && msg.payload.choices && msg.payload.choices.length > 0) {
    const extractedText = msg.payload.choices[0].message.content.trim();

    // Parse AI extraction results
    let intent = "Email needs processing";
    let keyInfo = "View detailed content";
    let action = "View detailed analysis";

    // Extract intent
    const intentMatch = extractedText.match(/Intent[Ôºö:](.+?)(?=\n|Key Information|Action|$)/s);
    if (intentMatch) {
        intent = intentMatch[1].trim();
    }

    // Extract key information
    const keyInfoMatch = extractedText.match(/Key Information[Ôºö:](.+?)(?=\n|Action|$)/s);
    if (keyInfoMatch) {
        keyInfo = keyInfoMatch[1].trim();
    }

    // Extract action
    const actionMatch = extractedText.match(/Action[Ôºö:](.+?)(?=\n|$)/s);
    if (actionMatch) {
        action = actionMatch[1].trim();
    }

    msg.payload = {
        intent: intent,
        keyInfo: keyInfo,
        action: action,
        extractedText: extractedText
    };
} else {
    // Response format abnormal, use default values
    msg.payload = {
        intent: "Email needs processing",
        keyInfo: "View detailed content",
        action: "View detailed analysis"
    };
    node.warn("AI response format abnormal, using default values");
}

// Restore original data for subsequent processing
msg.emailSummary = msg.originalAnalysis.emailSummary;
msg.fullAnalysis = msg.originalAnalysis.aiAnalysis;

return msg;

// Build history record
const historyRecord = {
    id: Date.now().toString(),
    timestamp: new Date().toISOString(),
    date: new Date().toLocaleString('en-US'),
    emailInfo: {
        from: msg.emailSummary.from,
        subject: msg.emailSummary.subject,
        preview: msg.emailSummary.preview,
        messageId: msg.emailSummary.messageId
    },
    analysis: {
        intent: msg.payload.intent || 'Not analyzed',
        keyInfo: msg.payload.keyInfo || 'No key information',
        action: msg.payload.action || 'No action suggestion'
    },
    fullAnalysis: msg.fullAnalysis || 'Full analysis unavailable'
};

// Get existing history records
let emailHistory = context.get('emailHistory') || [];

// Add new record to beginning
emailHistory.unshift(historyRecord);

// Get user-set retention count, default 30 records
const maxRecords = global.get('emailHistoryLimit') || 30;

// Maintain specified number of records
if (emailHistory.length > maxRecords) {
    emailHistory = emailHistory.slice(0, maxRecords);
}

// Save updated history
context.set('emailHistory', emailHistory);

// Update statistics
let stats = context.get('emailStats') || { totalProcessed: 0, lastUpdate: null };
stats.totalProcessed += 1;
stats.lastUpdate = new Date().toISOString();
context.set('emailStats', stats);

// Set node status display
node.status({
    fill: "blue",
    shape: "dot",
    text: `Saved ${emailHistory.length}/${maxRecords} records`
});

// Log record
node.log(`History record saved - Currently ${emailHistory.length} records`);

// Pass data to next node
msg.historyInfo = {
    saved: true,
    recordId: historyRecord.id,
    totalRecords: emailHistory.length,
    historyRecord: historyRecord
};

return msg;

// Build email notification data
const intent = msg.payload.intent || "Email needs processing";
const keyInfo = msg.payload.keyInfo || "View detailed content";
const action = msg.payload.action || "View detailed analysis";
const from = msg.emailSummary.from || "Unknown sender";
const subject = msg.emailSummary.subject || "No subject";
const preview = msg.emailSummary.preview || "No content preview";

// Build notification object
const notification = {
    id: Date.now(),
    timestamp: new Date().toISOString(),
    displayTime: new Date().toLocaleString('en-US'),
    from: from,
    subject: subject,
    preview: preview,
    analysis: {
        intent: intent,
        keyInfo: keyInfo,
        action: action
    },
    status: 'unread'
};

// Get existing notifications
let notifications = global.get('emailNotifications') || [];

// Add new notification to beginning
notifications.unshift(notification);

// Keep recent 20 notifications
if (notifications.length > 20) {
    notifications = notifications.slice(0, 20);
}

// Save notification list
global.set('emailNotifications', notifications);

// Pass data to HTTP response
msg.payload = {
    success: true,
    notification: notification,
    totalNotifications: notifications.length
};

node.log(`New email notification added: ${subject}`);

return msg;

const notifications = global.get('emailNotifications') || [];
const today = new Date().toDateString();
const todayCount = notifications.filter(n => new Date(n.timestamp).toDateString() === today).length;
const lastUpdate = notifications.length > 0 ? new Date(notifications[0].timestamp).toLocaleTimeString('en-US', {hour: '2-digit', minute: '2-digit'}) : '--:--';

msg.payload = {
    success: true,
    total: notifications.length,
    todayCount: todayCount,
    lastUpdate: lastUpdate,
    notifications: notifications
};

msg.statusCode = 200;
msg.headers = {'Content-Type': 'application/json'};
return msg;

const { id } = msg.payload;
if (!id) {
    msg.payload = { success: false, error: 'Missing notification ID' };
    msg.statusCode = 400;
    return msg;
}

let notifications = global.get('emailNotifications') || [];
const originalLength = notifications.length;
notifications = notifications.filter(n => n.id != id);
global.set('emailNotifications', notifications);

if (notifications.length < originalLength) {
    msg.payload = { success: true, message: 'Notification deleted successfully', remainingCount: notifications.length };
    node.log(`Notification deleted successfully: ${id}`);
} else {
    msg.payload = { success: false, error: 'Specified notification not found' };
}

msg.statusCode = 200;
msg.headers = { 'Content-Type': 'application/json' };
return msg;

const notifications = global.get('emailNotifications') || [];
const clearedCount = notifications.length;
global.set('emailNotifications', []);

msg.payload = { success: true, message: `All notifications cleared`, clearedCount: clearedCount };
msg.statusCode = 200;
msg.headers = { 'Content-Type': 'application/json' };
node.log(`All notifications cleared: ${clearedCount} items`);
return msg;
